
import numpy as np

def getVal(xmlString, name):
    results = np.array([])
    ell = len(name)
    for i in range(len(xmlString) - ell):
        if xmlString[i:i+ell] == name:
            j = i + ell
            tmp = xmlString[j]
            while (tmp[-1] != "<"):
                j = j + 1
                tmp = tmp + xmlString[j]
            results = np.hstack(( results, tmp[:-1] ))
    return results

