
import numpy as np

def getVal(xmlString, name):
    results = np.array([])
    ell = len(name)
    for i in range(len(xmlString) - ell):
        if xmlString[i:i+ell] == name:
            j = i + ell
            tmp = xmlString[j]
            while (tmp[-1] != "<"):
                j = j + 1
                tmp = tmp + xmlString[j]
            results = np.hstack(( results, tmp[:-1] ))
    return results

def pretty(xmlString):
    begin = True
    newline = "\n"
    p = xmlString[0]
    indent = 0
    for i in range(1, len(xmlString) - 1):
        if (xmlString[i] == "<"):
            p = p + newline
            if (xmlString[i+1] == "/"):
                begin = not begin
                indent = indent - 4
            elif begin:
                indent = indent + 4
            for j in range(indent):
                p = p + " "
        elif xmlString[i-1] == ">":
            p = p + newline
            indent = indent + 4
            for j in range(indent):
                p = p + " "
        p = p + xmlString[i]
    return p + xmlString[-1]
